/* Create a custom checkbox */
.coupon-list li{
    position: relative;
    padding-left: 30px;
    list-style: none;
    margin-bottom: 15px;
}
.coupon-list li [type="radio"]{
    opacity: 0;
}
.coupon-list{
    font-size: 16px;
    cursor: pointer;    
}
.checkmark {
  position: absolute;
  top: 5px;
  left: 0;
  height: 20px;
  width: 20px;
  background-color: #fff;border-radius: 6px;
  border: 1px solid #68B186;
  cursor: pointer;
}

.code_input:hover input ~ .checkmark {
  background-color: #68B186;
}
.code_input input[checked="checked"] ~ .checkmark {
  background-color: #68B186;
}
.checkmark:after {
  content: "";
  position: absolute;
  display: none;
}
.code_input input[checked="checked"] ~ .checkmark:after {
  display: block;
}
.code_input .checkmark:after {
    left: 7px;
    top: 4px;
    width: 6px;
    height: 10px;
    border: solid white;
    border-width: 0 2px 2px 0;
    -webkit-transform: rotate(45deg);
    -ms-transform: rotate(45deg);
    transform: rotate(45deg);
}
