          }
         if(ids == ''){
                  ids = Product_Id;
                }else{
                  ids = ids+','+Product_Id;
                }
              }
              var query = `query ExtendedProductsById {
          site {
            products(entityIds: [`+ ids + `]) {
              edges {
                node {
                  id
                  entityId
                  name
                  inventory{
                  isInStock
                  aggregated{
                    availableToSell
                    warningLevel
                  }
                }
                  categories
                  {
                  edges
                    {
                      node
                      {
                        id
                        name
                        
                      }
                    } 
                  }
                  
                  prices (includeTax:true){
                    price {
                      ...MoneyFields
                    }
                    priceRange {
                      min {
                        ...MoneyFields
                      }
                      max {
                        ...MoneyFields
                      }
                    }
                    salePrice {
                      ...MoneyFields
                    }
                    retailPrice {
                      ...MoneyFields
                    }
                    saved {
                      ...MoneyFields
                    }
                    bulkPricing {
                      minimumQuantity
                      maximumQuantity
                      ... on BulkPricingFixedPriceDiscount {
                        price
                      }
                      ... on BulkPricingPercentageDiscount {
                        percentOff
                      }
                      ... on BulkPricingRelativePriceDiscount {
                        priceAdjustment
                      }
                    }
                  }
                }
              }
            }
          }
        }
        fragment MoneyFields on Money {
              value
              currencyCode
            }`;
          $.ajax({
            url: "/graphql", 
            contentType: "application/json",
            type: 'POST',
            async: false,
            headers: {
